import React, { useState } from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

function Tracking() {
  const [currentLocation, setCurrentLocation] = useState({ lat: 37.7749, lng: -122.4194 }); // Default to San Francisco
  const [isTrackingActive, setIsTrackingActive] = useState(false);

  const startTracking = () => {
    setIsTrackingActive(true);
    // Simulate tracking by updating location periodically
    const trackingInterval = setInterval(() => {
      const newLat = currentLocation.lat + 0.01 * (Math.random() - 0.5);
      const newLng = currentLocation.lng + 0.01 * (Math.random() - 0.5);
      setCurrentLocation({ lat: newLat, lng: newLng });
    }, 3000); // Update every 3 seconds

    return () => {
      setIsTrackingActive(false);
      clearInterval(trackingInterval);
    };
  };

  return (
    <div className="content">
      <h2>Shuttle Tracking</h2>
      <div style={containerStyle}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentLocation}
          zoom={15}
        >
          {isTrackingActive && <Marker position={currentLocation} />}
        </GoogleMap>
      </div>
      {isTrackingActive ? (
        <button onClick={startTracking} disabled>
          Tracking in progress
        </button>
      ) : (
        <button onClick={startTracking}>Start Tracking</button>
      )}
    </div>
  );
}

export default Tracking;
